class Series:
    def __init__(self, title: str, seasons: int, genres: list):
        self.title = title
        self.seasons = seasons
        self.genres = genres
        self.ratings = []

    def __str__(self):
        genre_str = ", ".join(self.genres)
        if not self.ratings:
            rating_str = "no ratings"
        else:
            avg = sum(self.ratings) / len(self.ratings)
            rating_str = f"{len(self.ratings)} ratings, average {avg:.1f} points"
        return f"{self.title} ({self.seasons} seasons)\ngenres: {genre_str}\n{rating_str}"

    def rate(self, rating: int):
        self.ratings.append(rating)

def minimum_grade(rating: float, series_list: list):
    return [s for s in series_list if s.ratings and sum(s.ratings)/len(s.ratings) >= rating]
